mapper.insert:
    	INSERT 
    	  INTO STORE
    		   (
				sto_name
				, sto_tel
				, sto_loc
				, sto_no_acc 
				, sto_gps_x
				, sto_gps_y
				, sto_comment
				, sto_photo
				, sto_thumbPhoto
    		   )
        VALUES (
        		#{sto_name}
        		, #{sto_tel}
        		, #{sto_loc}
        		, #{sto_no_acc}
        		, #{sto_gps_x}
        		, #{sto_gps_y}
        		, #{sto_comment}
        		, #{sto_photo}
        		, #{sto_thumbPhoto}
    		   )

mapper.update:
UPDATE STORE 
   SET sto_name = #{sto_name}, sto_tel = #{sto_tel}, sto_loc = #{sto_loc}, sto_gps_x = #{sto_gps_x}, sto_gps_y = #{sto_gps_y},
   	   sto_comment = #{sto_comment}
 WHERE sto_no = #{sto_no}

mapper.select:
SELECT sto_no
  FROM STORE
 WHERE sto_no_acc = #{sto_no_acc}

mapper.insert:
        INSERT 
    	  INTO menu
    		   (
				menu_no_sto
				, menu_name
				, menu_price
				, menu_check
    		   )
        VALUES (
				#{menu_no_sto}
				, #{menu_name}
				, #{menu_price}
				, #{menu_check}
    		   )

mapper.select:
SELECT sto_no
  FROM store
 WHERE sto_no_acc = #{sto_no_acc}

mapper.update:
UPDATE reservation 
   SET res_check = #{res_check}
 WHERE res_no = #{res_no}

UPDATE TB
   SET tb_curTable = tb_curTable + 1
 WHERE tb.sto_no_tb = #{res_no_sto};

mapper.select:
SELECT tb_maxTable
  FROM TB
 WHERE sto_no_tb = #{res_no_sto};
