mapper.select:
SELECT let_no, let_title, let_content, let_date, let_viewCnt
  FROM letter
 WHERE let_no = #{let_no}

mapper.update:
UPDATE letter 
   SET let_title= #{let_title}, let_content = #{let_content}
 WHERE let_no = #{let_no}

UPDATE letter
   SET let_viewCnt = let_viewCnt + 1 
 WHERE let_no = #{let_no}
