mapper.insert:
	INSERT 
	  INTO ACCOUNT
		   (
			acc_id
			, acc_pw
			, acc_name
			, acc_level
		   )
    VALUES (
    		#{acc_id}
    		, #{acc_pw}
    		, #{acc_name}
    		, #{acc_level}
		   )

mapper.select:
SELECT acc_name 
  FROM ACCOUNT
 WHERE acc_id = #{acc_id} 
   AND acc_pw = #{acc_pw}

SELECT acc_name
       , acc_id 
  FROM ACCOUNT
 WHERE acc_id = #{acc_id} 
   AND acc_pw = #{acc_pw}
