mapper.insert:
	INSERT 
	  INTO ACCOUNT
		   (
			acc_id
			, acc_pw
			, acc_name
			, acc_level
			, acc_count
			, acc_date
		   )
    VALUES (
    		#{acc_id}
    		, #{acc_pw}
    		, #{acc_name}
    		, #{acc_level}
    		, 0
    		, now()
		   )

	INSERT
	  INTO STORE
	       (
	       	sto_name
	       	, sto_tel
	       	, sto_loc
	       	, sto_gps_x
	       	, sto_gps_y
	       )
	 VALUE (
	 		#{sto_name}
    		, #{sto_tel}
    		, #{sto_loc}
    		, #{sto_gps_x}
    		, #{sto_gps_y}
	 	   )

mapper.select:
SELECT acc_name 
  FROM ACCOUNT
 WHERE acc_id = #{acc_id} 
   AND acc_pw = #{acc_pw}

mapper.update:
UPDATE ACCOUNT
   SET acc_count = acc_count + 1 
 WHERE acc_id = #{acc_id}
   AND acc_pw =	#{acc_pw}

UPDATE ACCOUNT
   SET acc_date = now()
 WHERE acc_id = #{acc_id}
   AND acc_pw = #{acc_pw}

mapper.select:
SELECT COUNT(*)
  FROM ACCOUNT
 WHERE acc_id = #{acc_id}
