mapper.select:
SELECT res_no
  FROM RESERVATION
 WHERE res_no_acc = #{review_no_acc}
   AND res_no_sto = #{review_no_sto}
   AND res_check = 1;

SELECT count(*)
  FROM review
 WHERE review_no_acc = #{review_no_acc}
   AND review_no_sto = #{review_no_sto}

mapper.insert:
		INSERT 
    	  INTO REVIEW
    		   (
    			review_no_sto
    			, review_no_acc
    			, review_level_acc
    			, review_content
    			, review_star
    			, review_date
    			, review_tag
    			, review_like
    		   )
        VALUES (
        		#{review_no_sto}
        		, #{review_no_acc}
        		, #{review_level_acc}
        		, #{review_content}
        		, #{review_star}
        		, now()
        		, #{review_tag}
        		, #{review_like}
    		   )
